void printImage()
{
	int i, j;

	for(i = 0; i < lineImages; i++)
	{
		for(j = 0; j < 48; j++)
			printf("%d ", t_image[i][j]);
		printf("\n");
	}
}

float sum(unsigned char* imageLine)
{
	float result = 0;
	int i;

	result += sinWeight[0] * VLR_BIAS;

	for(i = 1; i < nCol; i++)
		result += sinWeight[i]*imageLine[i-1];

	return result;
}

int step(float s)
{
	if(s > 1.0) return 1;
	return 0;
}

float deltaRule(int error, int entry, float S)
{
	return (S + (TX_APR * error * entry));
}

void testing()
{
    int i,j,k;
	int response;
	int tx = 0;
	float sum = 0.0;

  for(k = 0; k < 1; k++)
  {
	    for(i = k*nLine; i < nLine*(k+1); i++)
		{
			for(j = 1; j < nCol; j++)
		    	sum += sinWeight[j]*ts_image[i][j-1];

		    sum += sinWeight[0];
		    response = step(sum);

		    if(response == ts_image[i][nCol-1] )
		    	tx++;

		    sum = 0.0;
		}

	printf("Taxa de acerto para imagem %d: %f\n", k+1, ((tx*1.0)/(48*1.0))*100);
	tx = 0;
  }

}
void traininig()
{
	int i, j, k, response, error;

	for(i = 0; i < AGES; i++){

		for(j = 0; j < nLine; j++){

			response = step(sum(t_image[j]));
			error = t_image[j][nCol-1] - response;

			if(error)
			{
				sinWeight[0] = deltaRule(error,VLR_BIAS,sinWeight[0]);
				
				for(k = 1; k < nCol; k++)
					sinWeight[k] = deltaRule(error,t_image[j][k-1], sinWeight[k]);
			}
		}

	}

}

void printSinapticWeightVector(int n)
{
    int i;

    for(i = 0; i < n; i++)
        printf("%f ", sinWeight[i]);
    printf("\n");

}

int main (int argc, char *argv[])
{

	printImage();
	
	traininig();
	
	printSinapticWeightVector(49);
	
	testing();

	return 0;
}
